import Card from './Card';
import styles from './Cards.module.css';
export default function Cards({characters, onClose}) {
   return <div className={styles.flexContainer}>{characters.map(char =>(
      <Card
         key={char.id}
         name={char.name}
         status={char.status}
         species={char.species}
         gender={char.gender}
         origin={char.origin}
         image={char.image}
         onClose={() => onClose(char.id)}
      />
   ))

 }</div>;
}
