import axios from "axios";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import styles from "./Detail.module.css"

export default function Detail () {
    const {id} = useParams();
    const [character, setCharacter] = useState({});
    const [translateX, setTranslateX] = useState(0);
    const [translateY, setTranslateY] = useState(0);
    useEffect(() => {
        axios(`https://rickandmortyapi.com/api/character/${id}`).then(({ data }) => {
           if (data.name) {
              setCharacter(data);
           } else {
              window.alert('No hay personajes con ese ID');
           }
        });
        return setCharacter({});
     }, [id]);

    useEffect(() => {
    const interval = setInterval(() => {
      setTranslateX(Math.floor(Math.random() * window.innerWidth));
      setTranslateY(Math.floor(Math.random() * window.innerHeight));
    }, 5000);

    return () => clearInterval(interval);

  }, []);
    return (
        <div className={styles.detailDiv}>
            <h1>Detail personaje</h1>
            <h2
        style={{
          animation: "move 5s infinite",
          transform: `translate(${translateX}px, ${translateY}px)`,
        }}
      >
        {character.name}
      </h2>
            <h2 className={styles.detailItem}>{character.status}</h2>
            <h2 className={styles.detailItem}>{character.species}</h2>
            <h2 className={styles.detailItem}>{character.gender}</h2>

            <img src={character.image}></img>
        </div>
    )
}
