import axios from "axios";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import styles from "./Detail.module.css";

export default function Detail() {
  const { id } = useParams();
  const [character, setCharacter] = useState({});
  const [translateX, setTranslateX] = useState(0);
  const [translateY, setTranslateY] = useState(0);

  useEffect(() => {
    axios(`https://rickandmortyapi.com/api/character/${id}`).then(({ data }) => {
      if (data.name) {
        setCharacter(data);
      } else {
        window.alert("No hay personajes con ese ID");
      }
    });

    const interval = setInterval(() => {
      setTranslateX(Math.floor(Math.random() * (window.innerWidth - 200)));
      setTranslateY(Math.floor(Math.random() * (window.innerHeight - 200)));
    }, 5000);

    return () => clearInterval(interval);
  }, [id]);

  return (
    <>
    <div className={styles.detailDiv}>
        <div>
        <h1 className={styles.detailTitle}>Detalle personaje</h1>
        </div>
        <div className={styles.detailCard}>
        <h2
        style={{
          animation: "move 5s infinite",
          transform: `translate(${translateX}px, ${translateY}px)`,
        }}
        >
            {character.name}
        </h2>
        <h2 style={{
            animation: "move 5s infinite",
            transform: `translate(${translateX}px, ${translateY}px)`,
            }}>{character.status}</h2>
        <h2 style={{
            animation: "move 5s infinite",
            transform: `translate(${translateX}px, ${translateY}px)`,
            }}>{character.species}</h2>
        <h2 style={{
            animation: "move 5s infinite",
            transform: `translate(${translateX}px, ${translateY}px)`,
            }}>{character.gender}</h2>

        <img src={character.image} alt={character.name} className={styles.detailImg}/>
        </div>
    </div>
    </>
  );
}
