const ADD_FAV = "ADD_FAV";
const REMOVE_FAV = "REMOVE_FAV";

export const addFav = (personaje) => {
    return {
        type: ADD_FAV,
        payload: personaje,
    }
}

export const removeFav = (id) => {
    return {
        type: REMOVE_FAV,
        payload: id,
    }
}
