import axios from "axios";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import styles from "./Detail.module.css";

export default function Detail() {
  const { id } = useParams();
  const [character, setCharacter] = useState({});

  useEffect(() => {
    axios(`https://rickandmortyapi.com/api/character/${id}`)
      .then(({ data }) => {
        if (data.name) {
          setCharacter(data);
        } else {
          window.alert("No hay personajes con ese ID");
        }
      })
      .catch(error => {
        console.error("Error al obtener los datos del personaje:", error);
      });
  }, [id]);

  const backgroundClass = () => {
    if (character.gender === "male") {
      return styles.male;
    } else if (character.gender === "female") {
      return styles.female;
    } else {
      return styles.other;
    }
  };

  return (
    <>
      <div className={styles.detailDiv}>
        <div className={styles.titleDiv}>
          <h1 className={styles.detailTitle}>Bro's details of: <br /> {character.name}</h1>
        </div>
        <div className={`${styles.detailCard} ${backgroundClass()}`}>
          <div>
            <h2>{character.status}</h2>
            <h2>{character.species}</h2>
            <h2>{character.gender}</h2>
          </div>
          <img src={character.image} alt={character.name} className={styles.detailImg} />
        </div>
      </div>
    </>
  );
}
